export const getRandomWords = async () => {
  try {
    const words = await axios.get(
      `https://api.wordnik.com/v4/words.json/randomWords?hasDictionaryDef=true&maxCorpusCount=-1&minDictionaryCount=1&maxDictionaryCount=-1&minLength=5&maxLength=-1&limit=11&api_key=m20ta3a4gg46fpd5nnd2b1q0g2jtimzmvyqqd3te15yhb0u49`
    );
    return words.data.words;
  } catch (error) {
    console.log(error);
  }
};

export const getWordDefinition = async (word) => {
  try {
    const wordDef = await axios.get(
      "http://localhost:9090/word-definition",

      {
        word,
      }
    );
    return wordDef.data.definition;
  } catch (error) {
    console.log(error);
  }
};
